<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Signin extends CI_Controller {	public function __construct()	{	parent::__construct();        $this->load->model(array('backend/Model_menu_frontend','web/Model_register','backend/Model_logcms'));	$this->load->helper(array('funcglobal','captcha'));        if( ! $_SESSION)                {                    session_start();                }	}		public function index()	 {            include 'checkSession.php';             if ($session_login == ""){                    if ($_POST) {                              $email = $this->security->xss_clean(secure_input($_POST['email']));                            $password = $this->security->xss_clean(secure_input_password($_POST['password']));                            $pass = md5($password);                            $getSign = $this->Model_register->cekSignUp($email,$pass);                             if(!empty($getSign)){                            foreach($getSign as $items){                                 $session_email = $items->agent_id."|".$items->first_name."|".$items->email;                              }                            $_SESSION['user_data'] = $session_email;                            $data = 3;                            }                            else { $data = 4; }                    }                    else { $data = 4; }                }                else { $data = 4; }                echo json_encode($data);    }	function signout()	{		//$this->session->unset_userdata(array("searchkey" => '', "searchby" => ''));		session_destroy();		redirect(BASE_URL."/home");		exit();	}}