<?php$session_login = isset($_SESSION['user_data']) ? $_SESSION['user_data'] : '';if ($session_login != "") {    $pecah                          = explode("|", $session_login);    $this->data["quest_id"]         = $pecah[0];    $this->data["quest_name"]       = $pecah[1];    $this->data["quest_phone"]     = $pecah[2];    $this->data["quest_email"]      = $pecah[3];    $this->data["checkin_date"]     = $pecah[4];    $this->data["showroom_name"]    = $pecah[5];    $this->data["showroom_address"] = $pecah[6];     $this->data["checkin_id"]      = $pecah[7];     } else {    $this->data["quest_id"]         = '';    $this->data["quest_name"]       = '';    $this->data["quest_phone"]      = '';    $this->data["quest_email"]      = '';    $this->data["checkin_date"]     = '';    $this->data["showroom_name"]    = '';    $this->data["showroom_address"] = '';    $this->data["checkin_id"]       = '';}