<script type="text/javascript">// fire the flexslider script (please ensure flexslider.js)    // Can also be used with $(document).ready()    $(window).load(function() {      $('.slides').flexslider({        animation: "fade",// -----------------------------------// Lazy-Load type function        before: function(slider){            //alert(slider.slides[slider.animatingTo].childNodes[0].src);            if( $(slider.slides[slider.animatingTo].childNodes[0]).attr("src") == "")                 { $(slider.slides[slider.animatingTo].childNodes[0]).attr("src", $(slider.slides[slider.animatingTo].childNodes[0]).attr("rel")); }        },        start: function(slider){            //alert( $(slider.slides[slider.currentSlide].childNodes[0]).attr("rel") );            if( $(slider.slides[slider.currentSlide].childNodes[0]).attr("src") == "")                 { $(slider.slides[slider.currentSlide].childNodes[0]).attr("src", $(slider.slides[slider.currentSlide].childNodes[0]).attr("rel")); }        }// -----------------------------------      });    });</script><div id="slide-container" class="clearfix">        <div class="flexslider"><!--id="slides"-->            <div class="flex-viewport">            <ul class="slides">                <?php if($banner > 0){ ?>                <?php $i = 1;foreach($banner as $ban){ ?>                    <li>                        <img src="<?php  echo BASE_URL.'/'. $ban->content_gallery_image;?>"  alt="<?php $ban->content_gallery_title;?>" draggable="false">                    </li><!--class="slides-container"-->                <?php $i++;} ?>	                <?php } else { ?>                     <li>                        <img src="<?php echo IMAGES_BASE_URL ;?>/image-default.jpg"  alt="image default" draggable="false">                    </li><!--class="slides-container"-->                <?php } ?>             </ul>                                                                    </div>        </div>            </div>                             