<div id="map"></div>   <script>      function initMap() {        var bestwestern = {lat: <?php echo PROJECT_LAT;?>, lng: <?php echo PROJECT_LONG;?>};        var map = new google.maps.Map(document.getElementById('map'), {          zoom: 18,          center: bestwestern,          scrollwheel:  false        });        var contentString =             '<div class="col-md-12 map-box">'+                '<div class="col-md-4 logo-map center">'+                '<p></p>'+                '<p></p>'+                    '<div class="logo-map-inner">'+                        '<img src="<?php echo IMAGES_BASE_URL;?>/logo-best-western-standard-map.png" width="100">'+                    '</div>'+                '</div>'+                '<div class="col-md-8 map-address">'+                    '<h3 class="firstHeading"><?php echo PROJECT_NAME;?></h3>'+                                        '</br> <?php echo PROJECT_ADDR1;?> </br> <?php echo PROJECT_ADDR2;?></span> <br/>' +                    '<p>'+                    'Telp :<?php echo PROJECT_TELP;?><br/>'+                    'Fax :<?php echo PROJECT_FAX;?><br/>'+                    'Email :<?php echo MAIL_SENDER;?><br/>'+                     '</p>'+                '</div>'+            '</div>';        var infowindow = new google.maps.InfoWindow({          content: contentString        });        var image = '<?php echo IMAGES_BASE_URL;?>/location.png';        var beachMarker = new google.maps.Marker({          position: bestwestern,          map: map,          icon: image        });         infowindow.open(map,beachMarker);         beachMarker.addListener('click', function() {          infowindow.open(map, beachMarker);        });      }    </script>    <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBPKdNjK2IR-CdsJjlzf_-pTPIekm5Py3E&callback=initMap"    async defer></script>